import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

// Expanded type definitions to handle D3 force simulation properly
interface NodeDatum extends d3.SimulationNodeDatum {
  id: number;
  firstname?: string;
  lastname?: string;
  nationality?: string;
  exhibitions_count?: number;
  weight?: number;
}

interface LinkDatum extends d3.SimulationLinkDatum<NodeDatum> {
  source: number | NodeDatum;
  target: number | NodeDatum;
  weight?: number;
}

interface GraphData {
  nodes: NodeDatum[];
  links: LinkDatum[];
}

const ArtVisCollaborationNetwork: React.FC = () => {
  const svgRef = useRef<SVGSVGElement | null>(null);
  const tooltipRef = useRef<HTMLDivElement | null>(null);
  const [data, setData] = useState<GraphData | null>(null);
  const [minEdgeWeight, setMinEdgeWeight] = useState<number>(20);
  const [sortConfig, setSortConfig] = useState<{
    key: 'id' | 'nationality' | 'exhibitions_count';
    direction: 'asc' | 'desc';
  } | null>(null);

  const fetchCollaborationData = async () => {
    try {
      const response = await fetch('/data/artist_collaboration_network.json');
      if (!response.ok) throw new Error('Failed to fetch network data');
      const jsonData: GraphData = await response.json();

      // Calculate total weight of connections for each node
      const nodeWeights = new Map<number, number>();
      
      jsonData.links.forEach(link => {
        // Safely extract source and target IDs
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        const weight = link.weight || 0;

        // Ensure we're working with numbers
        if (typeof sourceId === 'number' && typeof targetId === 'number') {
          nodeWeights.set(sourceId, (nodeWeights.get(sourceId) || 0) + weight);
          if (sourceId !== targetId) {
            nodeWeights.set(targetId, (nodeWeights.get(targetId) || 0) + weight);
          }
        }
      });

      // Add weights to nodes
      jsonData.nodes = jsonData.nodes.map(node => ({
        ...node,
        weight: nodeWeights.get(node.id) || 0,
        exhibitions_count: nodeWeights.get(node.id) || 0
      }));

      setData(jsonData);
    } catch (error) {
      console.error('Error fetching network data:', error);
      setData(null);
    }
  };

  const filteredData = () => {
    if (!data) return { nodes: [], links: [] };

    // Filter out self-loops and low weight connections
    const validLinks = data.links.filter(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return (link.weight || 0) >= minEdgeWeight && sourceId !== targetId;
    });

    // Create a Set of used node IDs, ensuring they're all numbers
    const usedNodeIds = new Set(
      validLinks.flatMap(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return [sourceId, targetId];
      }).filter((id): id is number => typeof id === 'number')
    );

    // Keep only nodes that appear in remaining links
    const validNodes = data.nodes.filter(node => usedNodeIds.has(node.id));
    return { nodes: validNodes, links: validLinks };
  };

  useEffect(() => {
    fetchCollaborationData();
  }, []);

  // D3 visualization code
  useEffect(() => {
    if (!data || !svgRef.current) return;
  
    const { nodes, links } = filteredData();
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();
  
    const width = 900;
    const height = 500;
  
    // 1) Build a color scale by nationality. First, get all unique nationalities:
    const uniqueNationalities = Array.from(
      new Set(nodes.map(d => d.nationality || "Unknown"))
    );
    // Then map each nationality to one of d3’s Category10 colors:
    const colorScale = d3.scaleOrdinal<string>()
      .domain(uniqueNationalities)
      .range(d3.schemeCategory10);
  
    // Create the force simulation
    const simulation = d3
      .forceSimulation<NodeDatum>(nodes)
      .force('link', d3.forceLink<NodeDatum, LinkDatum>(links)
        .id(d => d.id)
        .distance(100))
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collide', d3.forceCollide().radius(30));
  
    const g = svg.append('g');
  
    // Draw links
    const maxWeight = d3.max(links, d => d.weight || 0) || 1;
    const linkScale = d3.scaleLinear()
      .domain([minEdgeWeight, maxWeight])
      .range([0.2, 1]);
  
    const linkSelection = g
      .selectAll<SVGLineElement, LinkDatum>('line')
      .data(links)
      .enter()
      .append('line')
      .attr('stroke', '#999')
      .attr('stroke-opacity', d => linkScale(d.weight || 0))
      .attr('stroke-width', d => Math.sqrt((d.weight || 1) / 5));
  
    // Draw nodes
    const maxExhibitions = d3.max(nodes, d => d.exhibitions_count || 0) || 1;
    const sizeScale = d3.scaleSqrt()
      .domain([0, maxExhibitions])
      .range([5, 20]);
  
      const nodeSelection = g
      .selectAll<SVGCircleElement, NodeDatum>('circle')
      .data(nodes)
      .enter()
      .append('circle')
      .attr('r', d => sizeScale(d.exhibitions_count || 0))
      // Always provide a fallback so TypeScript never sees undefined
      .attr('fill', d => colorScale(d.nationality || "Unknown"))
      .attr('stroke', '#fff')
      .attr('stroke-width', 2);
  
    // Tooltips
    const tooltip = d3.select(tooltipRef.current);
    nodeSelection
      .on('mouseover', (event, d) => {
        tooltip
          .style('visibility', 'visible')
          .html(`
            <strong>${d.firstname} ${d.lastname}</strong><br/>
            Nationality: ${d.nationality}<br/>
            Total Exhibitions: ${d.exhibitions_count}<br/>
          `);
      })
      .on('mousemove', (event) => {
        tooltip
          .style('top', `${event.pageY + 10}px`)
          .style('left', `${event.pageX + 10}px`);
      })
      .on('mouseout', () => {
        tooltip.style('visibility', 'hidden');
      });
  
    // 3) Build a legend: small circles plus text labels.
    //    We'll position it in the top-right corner of the svg.
    const legendG = svg.append('g')
      .attr('class', 'legend')
      .attr('transform', `translate(${width - 130}, 20)`);
  
    // Build the legend
    uniqueNationalities.forEach((nat, i) => {
      const legendRow = legendG.append('g')
        .attr('transform', `translate(0, ${i * 20})`);

      legendRow
        .append('circle')
        .attr('r', 6)
        .attr('fill', colorScale(nat));   // Now 'nat' is guaranteed a string

      legendRow
        .append('text')
        .attr('x', 12)
        .attr('y', 3) // approximate vertical centering
        .text(nat)
        .attr('font-size', '12px')
        .attr('fill', '#333');
    });
  
    // Zoom
    const zoom = d3.zoom<SVGSVGElement, unknown>()
      .scaleExtent([0.5, 4])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });
  
    svg.call(zoom);
  
    // Update positions on simulation tick
    simulation.on('tick', () => {
      linkSelection
        .attr('x1', d => (d.source as NodeDatum).x || 0)
        .attr('y1', d => (d.source as NodeDatum).y || 0)
        .attr('x2', d => (d.target as NodeDatum).x || 0)
        .attr('y2', d => (d.target as NodeDatum).y || 0);
  
      nodeSelection
        .attr('cx', d => d.x || 0)
        .attr('cy', d => d.y || 0);
    });
  }, [data, minEdgeWeight]);  

  return (
    <div className="p-5 flex flex-col items-center">
      <h1 className="text-2xl font-bold mb-4">Artist Collaboration Network</h1>
      
      <div className="w-full max-w-4xl">
        <svg
          ref={svgRef}
          width={900}
          height={500}
          className="border border-gray-300"
        />
        
        <div ref={tooltipRef} className="absolute hidden bg-white p-2 border border-gray-300 rounded shadow" />
        
        <div className="mt-4">
          <label className="block mb-2">
            Minimum Connection Weight: {minEdgeWeight}
            <input
              type="range"
              min="1"
              max="100"
              value={minEdgeWeight}
              onChange={e => setMinEdgeWeight(parseInt(e.target.value))}
              className="w-full"
            />
          </label>
        </div>
      </div>
    </div>
  );
};

export default ArtVisCollaborationNetwork;
